import "./ExpenseItem.css";

const ExpenseItem = (props) => {
  const renderExpenseList = props.expenses.map((expense) => {
    const month = expense.date.toLocaleString("en-US", { month: "long" });
    const day = expense.date.toLocaleString("en-US", { day: "2-digit" });
    const year = expense.date.getFullYear();
    return (
      <div className="expense-item">
        <div>
          <div>{month}</div>
          <div>{year}</div>
          <div>{day}</div>
        </div>
        <div className="expense-item__description">
          <h2>{expense.title}</h2>
          <div className="expense-item__price">Rs. {expense.amount}</div>
        </div>
      </div>
    );
  });
  return <div>{renderExpenseList}</div>;
};

export default ExpenseItem;
